	static private int m_paraid;

	static synchronized public String getPara() {
		int ix = m_paraid++;
		if(m_paraid >= LOREM.length)
			m_paraid = 0;
		return LOREM[ix];
	}

	static public String getSentences(int maxlength) {
		StringBuilder sb = new StringBuilder();
		String para = "";
		while(sb.length() < maxlength) {
			//-- We need to split another sentence off the current para
			if(para.isEmpty()) {
				para = getPara();
			}
			int pos = para.indexOf('.');
			String seg = para.substring(0, pos + 1).trim();
			para = para.substring(pos + 1).trim();
			if(sb.length() > 0)
				sb.append(' ');
			sb.append(seg);
		}
		return sb.toString();
	}
